sys_script_email.script:
(function runMailScript(current, template, email, email_action, event) {
	//printattachments();

	function printattachments() {
		var sysid = event.parm1;
		gs.info("Inside Print Attachments");
		gs.info("current sysid is:"+sysid);
		var gr = new GlideRecord('sys_attachment');
		gr.addQuery('table_sys_id',sysid);
		gr.query();
		while (gr.next()) {
			gs.info("Inside script  of email");
			template.print('Attachment: <a href="http://'+gs.getProperty("instance_name")+'.service-now.com/sys_attachment.do?sys_id='
			+ gr.sys_id + '">' + gr.file_name + '</a>');
			gs.info('Attachment: <a href="http://'+gs.getProperty("instance_name")+'.service-now.com/sys_attachment.do?sys_id='
			+ gr.sys_id + '">' + gr.file_name + '</a>');
		}
	}
})(current, template, email, email_action, event);
