sys_ui_action.script:
function runClientCode()
{
//	var sys_id=g_list.getChecked();
	var sys_id = g_form.getUniqueValue();
	var ga = new GlideAjax('AMPSScriptInclude');
	ga.addParam('sysparm_name', 'generateHardwareCheckList');
	ga.addParam('sysparm_sysid', sys_id);
	ga.getXML(getResponse);


}

function getResponse(response) {
	var message = response.responseXML.documentElement.getAttribute('answer');
	//gs.log('Message:' + message );
	if (message == 'Error')
		alert('Please select Yes/No for all the Hardwares to Replace');
	else if (message == 'Purchase Order already present') {
		alert('Purchase Order has already been created');
		}
	else {
	//	alert(message);
		var url = new GlideURL('/x_chfa_amps_purchase_order.do');
	//	url.addParam('sysparm_view','new');
		url.addParam('sys_id', message);
		//alert(url.getURL());
		//window.location.href = url.getURL();
	//	current.update();
top.window.location = 'x_chfa_amps_purchase_order.do?sys_id='+message;  

	//	action.setRedirectURL('x_chfa_amps_purchase_order.do?sys_id='+message + '&sysparm_view=&sysparm_record_target=x_chfa_amps_purchase_order' );

	}
}
