sysauto_script.script:
//Querying the location table
var gr = new GlideRecord('cmn_location');
gr.addEncodedQuery('u_pos_install_date!=NULL');
gr.query();
while(gr.next()){
	var ref = gr.getDisplayValue('u_pos_install_date');
	ref = new GlideDateTime(ref);

	var today = new GlideDateTime();
	// Duration in days
	var dur = GlideDateTime.subtract(ref , today); // returns a GlideDuration object
	var days = dur.getRoundedDayPart();
	//Comparing the duration between last installed date and current date
	if(days ==1)
		{
			//If POS requires refresh creating records in pos refresh table
		var gr1 = new GlideRecord('x_chfa_amps_pos_refresh');
		gr1.initialize();
		ref.addYearsUTC(5); //Setting the install date to 5 years from last install date.
		gr1.setValue('location',gr.sys_id);
		gr1.setValue('install_date',ref.getDate());

		gr1.insert();
	}
}
