sysauto_script.script:
var install_date = '';
var table = 'x_chfa_amps_pos_refresh';
var today = new GlideDateTime();
var dur ='';
var days = '';
var recordID ='';
//Defining the header for the checklist as per the template
var output = "Restaurant,Shipped back,Return #,Sch#,Manf.,Box #,UPS Tracking,Feature, Actual S/N,Equipment,Description,Notes,EISQ,Start Date,Lease End";

var gr = new GlideRecord('x_chfa_amps_pos_refresh');
gr.addEncodedQuery('location=ff2a896424cf20002a4da30681f3e382');
gr.query();
while(gr.next()){
	install_date = gr.getDisplayValue('install_date');
	install_date= new GlideDateTime(install_date);
	// Duration in days
	dur = GlideDateTime.subtract(today,install_date); // returns a GlideDuration object
	days = dur.getRoundedDayPart();
	gs.info("days is :"+days);
	if(days == 0)
		//if(days == gs.getProperty('x_chfa_amps.installation_checklist'))
	{
		var gr1=new GlideRecord('x_chfa_amps_hardware_checklist');
		gr1.addEncodedQuery('parent='+gr.sys_id);
		gr1.query();
		while(gr1.next()){
			//Generating the columns for the csv file
			output  += "\n" + gr1.getDisplayValue('parent') + ","
			+ "" + ","
			+ "" + ","
			+ "" + ","
			+ "" + ","
			+ "" + ","
			+ "" + ","
			+ "" + ","
			+ "" + ","
			+ gr1.asset.display_name + ","
			+ "" + ","
			+ "" + ","
			+ "" + ","
			+ "" + ","
			+ "";
			if (!recordID) {
				recordID = gr.sys_id;
			}

		}
		//Verifying if attachment already exists on the record. If not attach the installation checklist
		var gr2 = new GlideRecord('sys_attachment');
		gr2.addEncodedQuery('table_sys_id='+gr.sys_id);
		gr2.query();
		if(!gr2.next()){
			writeAttachmentFile(output);
			gs.eventQueue('x_chfa_amps.email_installation_checklist',gr);
		}
		gr.setValue('state',2);
		gr.update();
	}

}

function writeAttachmentFile(data) {
	var attachment = new global.Attachment();
	var attachmentRec = attachment.write(table, recordID, "Installation Checklist.csv", "text/csv", data);
}
