sysauto_script.script:
var install_date = '';
var today = new GlideDateTime();
var dur ='';
var days = '';
var gr = new GlideRecord('x_chfa_amps_pos_refresh');

gr.query();
while(gr.next()){
	install_date = gr.getDisplayValue('install_date');
	install_date= new GlideDateTime(install_date);
	// Duration in days
	dur = GlideDateTime.subtract(today,install_date); // returns a GlideDuration object
	days = dur.getRoundedDayPart();
	//Notifying vendor 6 weeks prior
	if(days == gs.getProperty('x_chfa_amps.initial_reminder')){		gs.eventQueue('x_chfa_amps.notify_vendor_six_weeks_prio',gr,gr.install_vendor.phone);
	}
	//Notifying vendor 3 weeks prior
	else if(days == gs.getProperty('x_chfa_amps.secondary_reminder'))
		gs.eventQueue('x_chfa_amps.notify_vendor_three_weeks_pr',gr,gr.install_vendor.phone);
	//Notifying vendor 1 week prior
	else if(days == gs.getProperty('x_chfa_amps.final_reminder'))
		gs.eventQueue('x_chfa_amps.notify_vendor_one_week_prior',gr,gr.install_vendor.phone);
	//Notify vendor on day of installation
	else if(days==0){
		gr.setValue('state',3);
		gr.update();
		gs.eventQueue('x_chfa_amps.notify_vendor_installation_d',gr,gr.install_vendor.phone);
	}

}
